class DirectStiffnessMethodException(Exception):
    pass

# %% ../nbs/03_Private_Modules/06_direct_stiffness_method.ipynb 9
class DirectStiffnessMethod:
    def __init__(
        self,
        *,
        bars,
        nodes,
        point_loads,
    ):
        self.bars = bars
        self.nodes = nodes
        self.point_loads = point_loads

